import React, { Component } from "react";
import styled from "styled-components";
var clearbit = require("clearbit")("sk_01bfcfe5af53f2bb4436cafbf35341b9");

const AppDiv = styled.div`
  background: #30c5ff;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { profile: "" };
  }

  componentDidMount() {
    this.fetchProfiles();
  }

  fetchProfiles = () => {
    /*  fetch("https://randomuser.me/api/")
      .then(function(response) {
        return response.json();
      })
      .then(myJson => {
        const { results } = myJson;
        console.log(results);

        this.setState({ profile: results[0] });
      }); */

    clearbit.Prospector.search({ domain: "twitter.com", role: "marketing" })
      .then(people => {
        const profile = people.results[0];
        this.setState({ profile });
      })
      .catch(function(err) {
        console.error(err);
      });
  };

  render() {
    const { profile } = this.state;

    const { role } = profile;
    /*  const {
      profile: { company: { name: companyName }, email, role, seniority, title }
    } = this.state; */
    console.log(profile, role);
    return (
      <AppDiv>
        <Card>{role}</Card>
      </AppDiv>
    );
  }
}

export default App;
