import React, { Component } from "react";
import styled from "styled-components";

// eslint-disable-next-line no-restricted-globals

const CardDiv = styled.div`
  background: white;
  width: 450px;
  height: 250px;
  border-radius: 3px;
  display: grid;
  border: 1px solid #efefef;
  padding: 10px 0 10px 0;
  grid-template-columns: 1fr 3fr;
  grid-template-rows: 1fr 1fr;
  grid-template-areas:
    "companyImage employeName"
    "companyTitle personalInfo";
  place-items: center;
  grid-column-gap: 10px;
  grid-row-gap: 15px;
`;

const LogoImage = styled.img`
  border-radius: 3px;
  grid-area: companyImage;
`;

const EmployeName = styled.h2`
  grid-area: employeName;
`;

const Card = ({ profile }) => {
  const {
    company: { name: companyName },
    role,
    name: { fullName },
    seniority
  } = profile;
  return (
    <CardDiv>
      <LogoImage src="https://logo.clearbit.com/segment.com" />
      <EmployeName> {fullName}</EmployeName>
    </CardDiv>
  );
};

export default Card;
