import React, { Component } from "react";
import styled from "styled-components";
import { capitalize } from "lodash";
import logo from "./logo.png";

const CardDiv = styled.div`
  background: white;
  width: 450px;
  height: 250px;
  border-radius: 3px;
  display: grid;
  grid-template-columns: 2fr 3fr;
  grid-template-rows: 1fr 1fr;
  grid-template-areas:
    "companyLogo employeName"
    "companyLogo personalInfo";
  place-items: center;
  grid-column-gap: 10px;
  grid-row-gap: 15px;
`;

const LogoImage = styled.img`
  border-radius: 3px;
  grid-area: companyLogo;
  height: 75%;
  width: 100%;
`;

const EmployeName = styled.h2`
  grid-area: employeName;
`;

const EmployeInfo = styled.div`
  grid-area: employeName;
  margin-left: 10px;
  display: flex;
  flex-direction: column;
  place-self: baseline;
  align-self: flex-end;
`;
const CompanyName = styled.div`
  grid-area: companyTitle;
  width: 100%;
  font-size: 32px;
  margin-left: 50px;
  color: #e1e1e0;
`;

const Name = styled.h2`
  font-size: 32px;
`;

const JobTitle = styled.p`
  margin-top: -27px;
`;

const Card = ({ profile }) => {
  const {
    company: { name: companyName },
    role,
    name: { fullName },
    seniority
  } = profile;
  return (
    <CardDiv>
      <LogoImage src={logo} />
      <EmployeInfo>
        <Name> {fullName}</Name>
        <JobTitle>
          {capitalize(role)} {capitalize(seniority)}
        </JobTitle>
      </EmployeInfo>
    </CardDiv>
  );
};

export default Card;
