import React, { Component } from "react";
import styled from "styled-components";
import { capitalize } from "lodash";

const CardDiv = styled.div`
  background: white;
  width: 450px;
  height: 250px;
  border-radius: 3px;
  display: grid;
  grid-template-columns: 2fr 3fr;
  grid-template-rows: 1fr 1fr;
  grid-template-areas:
    "companyImage employeName"
    "companyTitle personalInfo";
  place-items: center;
  grid-column-gap: 10px;
  grid-row-gap: 15px;
`;

const LogoImage = styled.img`
  border-radius: 3px;
  grid-area: companyImage;
`;

const EmployeName = styled.h2`
  grid-area: employeName;
`;

const EmployeInfo = styled.div`
  grid-area: personalInfo;
`;
const CompanyName = styled.div`
  grid-area: companyTitle;
`;

const Card = ({ profile }) => {
  const {
    company: { name: companyName },
    role,
    name: { fullName },
    seniority
  } = profile;
  return (
    <CardDiv>
      <LogoImage src="https://logo.clearbit.com/twitter.com" />
      <EmployeName> {fullName}</EmployeName>
      <EmployeInfo>
        {capitalize(role)} {capitalize(seniority)}
      </EmployeInfo>
      <CompanyName>{companyName}</CompanyName>
    </CardDiv>
  );
};

export default Card;
