import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { profile: "" };
  }

  fetchProfile = () => {
    fetch("https://randomuser.me/api/")
      .then(function(response) {
        return response.json();
      })
      .then(myJson => {
        const { results } = myJson;
        console.log(results);

        this.setState({ loading: false, profile: results[0] });
      });
  };

  componentDidMount() {
    this.fetchProfile();
  }

  render() {
    const { name, email, cell, loading } = this.state;
    console.log(this.state);
    return (
      <div className="App">
        {!loading && <div className="card">{email}</div>}
      </div>
    );
  }
}

export default App;
