import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { name, email, cell };
  }
  state = { name, email, cell };

  fetchGifs = () => {
    fetch("https://randomuser.me/api/")
      .then(function(response) {
        return response.json();
      })
      .then(myJson => {
        const { results } = myJson;
        console.log(results);
        const { name, email, cell, picture: { medium } } = results[0];
        this.setState({ loading: false, name, email, cell });
      });
  };

  componentDidMount() {
    this.fetchGifs();
  }

  render() {
    const { name, email, cell, loading } = this.state;
    console.log(this.state);
    return (
      <div className="App">
        {!loading && <div className="card">{name}</div>}
      </div>
    );
  }
}

export default App;
