import Card from "./Card";
import DownloadSignature from "./DownloadSignature";
import React, { Component } from "react";
import { PDFExport, savePDF } from "@progress/kendo-react-pdf";
import styled from "styled-components";
import { sample } from "lodash";
var clearbit = require("clearbit")("sk_01bfcfe5af53f2bb4436cafbf35341b9");

const AppDiv = styled.div`
  background: #e1e1e0;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { profile: "", loading: true };
  }

  componentDidMount() {
    this.fetchProfiles();
  }

  fetchProfiles = () => {
    clearbit.Prospector.search({
      domain: "www.twitter.com",
      role: "marketing"
    })
      .then(people => {
        const profile = sample(people.results);
        this.setState({ loading: false, profile });
      })
      .catch(function(err) {
        console.error(err);
      });
  };

  exportPDFWithComponent = () => {
    this.pdfExportComponent.save();
  };

  render() {
    const { profile, loading } = this.state;
    console.log(profile);
    return (
      <AppDiv>
        {loading && "Loading"}
        {!loading && (
          <PDFExport
            ref={component => (this.pdfExportComponent = component)}
            paperSize="A4"
          >
            {/*    <Grid
                ref={(grid) => this.grid = grid}
                style={{ maxHeight: '400px' }}
                data={this.state.gridData}
            >
                <Column field="ProductID" title="ID" width="40px" />
                <Column field="ProductName" title="Name" width="250px" />
                <Column field="Category.CategoryName" title="CategoryName" />
                <Column field="UnitPrice" title="Price" width="80px" />
                <Column field="UnitsInStock" title="In stock" width="80px" />
            </Grid> */}

            <Card id="signature" profile={profile} />
          </PDFExport>
        )}
        <button className="k-button" onClick={this.exportPDFWithComponent}>
          Export with component
        </button>
        <DownloadSignature id="signature" label="Download Signature" />
      </AppDiv>
    );
  }
}

export default App;
